msg.topic = "path";
msg.payload.device = flow.get("device").serial;
msg.payload.name = flow.get("name") || flow.get("device").hostname || flow.get("device").name;

var d = new Date(msg.payload.timestamp);
msg.payload.localTime = d.getFullYear() + '-';
msg.payload.localTime += ("00" + (d.getMonth() + 1)).slice(-2) + '-';
msg.payload.localTime += ("00" + d.getDay()).slice(-2) + ' ';
msg.payload.localTime += ("00" + d.getHours()).slice(-2) + ':';
msg.payload.localTime += ("00" + d.getMinutes()).slice(-2) + ':';
msg.payload.localTime += ("00" + d.getSeconds()).slice(-2);
return msg;

//Filter minimum distance
var distance = flow.get("distance") || 0;
if( msg.payload.distance < distance)  
    return;

//Filter minimum confidence
var confidence = flow.get("confidence") || 10;
if (msg.payload.confidence < confidence )
    return;

//Filter minimum age
var age = flow.get("age") || 0;
if( msg.payload.age < age)  
    return;

//Filter object class type
var classes= flow.get("classes");
var ignoreClass = true;
classes.forEach(function(element) {
    if( element === msg.payload.class)
        ignoreClass = false;
});
if(ignoreClass)
    return;

//Filter object passed Area Of Intrest
var aoi = flow.get("aoi") ||null;
if( aoi ) {
    var ignore = true;
    msg.payload.path.forEach(function(position) {
        if (ignore && 
            position.x >= aoi.x1 && 
            position.x <= aoi.x2 &&
            position.y >= aoi.y1 &&
            position.y <= aoi.y2)
                ignore = false;
    });
    if( ignore )
        return;
}

//All filters passed

return msg;

msg.payload = {
    device: flow.get("device").serial || "Undefined",
    timestamp: {
        $gte: new Date().getTime() - (24 * 3600 * 1000)
    }
}

return msg;

msg.options = [ "Human", "Car", "Truck", "Bus", "Motorcycle", "Vehicle", "Face", "License plate", "Object" ];
if( !flow.get("classes") ) {
    msg.payload = [ "Human", "Car", "Truck", "Bus", "Motorcycle", "Vehicle" ];
} else {
    msg.payload = flow.get("classes");
}


return msg;

msg.topic = "aoi";
msg.payload = flow.get("aoi") || {
    id: "aoi",
    x1: 200,
    y1: 200,
    x2: 800,
    y2: 800
}

return msg;

var days = flow.get("retention") || 31;
//Change above to desired number of days to retain data

msg.payload = {
    device: flow.get("device").serial || "Undefined",
    timestamp: {
        $lte: new Date().getTime() - (days * 24 * 3600 * 1000)
    }
}
return msg;

msg.topic = "image";
var image = msg.payload;
msg.payload = flow.get("camera");
msg.payload.image = image;
return msg;

msg.query = {
    device: flow.get("device").serial || "Undefined"
}

msg.payload = {
    device: flow.get("device").serial || "Undefined",
    name: flow.get("name") || flow.get("device").hostname || flow.get("device").name,
    timestamp: new Date().getTime(),
    rotation: msg.payload.rotation,
    aspect: msg.payload.aspect,
    image: msg.payload.image
}


return msg;

if( msg.payload !== "change")
    return;
if( msg.name !== "Path heatmap")
    return
return msg;

var camera = flow.get("camera");
switch(msg.topic) {
    case "aspect":
        camera.aspect = msg.payload;
        break;
    case "rotation":
        camera.rotation = msg.payload;
        break;
}

flow.set("camera",camera);

return msg;

msg.payload = flow.get("camera") || {
    aspect: "16:9",
    rotation: "Landscape",
    image: ""
}    
flow.set("camera",msg.payload);

return msg;

var camera = flow.get("camera");
if(!camera)
    return;

switch( camera.aspect ) {
    case "4:3":
        msg.resolution = "800x600"
        break;
    case "1:1":
        msg.resolution = "800x800"
        break;
    case "16:9":
    default:
        msg.resolution = "1280x720"
}

return msg;
